import React from 'react'
import { Link } from 'react-router-dom';
import { PageLinks, Button } from './';
import logo from '../images/shared/desktop/logo.svg';

const Navbar = () => {
    let { body } = document;

    
    const mobileNavOpen = (e) => {
        e.target.parentElement.classList.add('change');
        //e.target.nextSibling.classList.remove('spin');
        //mobileMenu.current.classList.toggle('deployed');
        //overlay.current.classList.add('active');
        body.style.overflowY = 'hidden';console.log(e.target.parentElement)
    }

    const mobileNavClose = e => {
        setTimeout(function() {
            e.target.parentElement.classList.remove('change')
        }, 850);
        //e.target.classList.add('spin');
        //mobileMenu.current.classList.toggle('deployed');
        //mobileMenu.current.style.transitionDelay = '450ms';
        //overlay.current.classList.remove('active');
        //overlay.current.style.transitionDelay = '450ms';
        body.style.overflowY = 'visible';
        e.persist();
    }

    return (
        <div className="navbar">
            <Link to="/">
                <img className="navbar__logo" src={logo} alt="Photosnap company logo" />
            </Link>
            <div className="navbar__links">
                <PageLinks />
            </div>
            <Button text="Get an invite" />
            <a href="#" className="navbar__mobile__btn" aria-label="Button for mobile menu deployment">
                <svg className="navbar__mobile__btn--open" onClick={mobileNavOpen} xmlns="http://www.w3.org/2000/svg" width="20" height="6">
                    <g fill-rule="evenodd" fill="#000"><path d="M0 0h20v1H0zM0 5h20v1H0z"/></g>
                </svg>
                <svg className="navbar__mobile__btn--close" onClick={mobileNavClose} xmlns="http://www.w3.org/2000/svg" width="16" height="15">
                    <path fill-rule="evenodd" fill="#000" d="M14.718.075l.707.707L8.707 7.5l6.718 6.718-.707.707L8 8.207l-6.718 6.718-.707-.707L7.293 7.5.575.782l.707-.707L8 6.793 14.718.075z"/>
                </svg>
            </a>
            {/* <div className="navbar__mobile__menu">
                <PageLinks />
            </div> */}
            <div  className="overlay" />
        </div>
    )
}

export default Navbar
