import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom';
import { PageLinks, Button } from './';
import logo from '../images/shared/desktop/logo.svg';
import open from '../images/shared/mobile/menu.svg';
import close from '../images/shared/mobile/close.svg';

const Navbar = () => {
    const [symbol, setSymbol] = useState(),
          [overlay, setOverlay] = useState(),
          [mobileMenu, setMobileMenu] = useState(),
          [menuOpen, setMenuOpen] = useState(false),
          { body } = document;
   
        
    const mobileNavOpen = () => {
        body.style.overflowY = 'hidden';
        body.parentElement.style.overflowY = 'hidden';
        hideMobileNav()
    }

    const mobileNavClose = () => {
        setTimeout(function() {
            symbol.classList.remove('change')
        }, 850);
        body.style.overflowY = 'visible';
        body.parentElement.style.overflowY = 'visible';
    }

    return (
        <div className="navbar">
            <Link to="/" onClick={() => setMenuOpen(false)}>
                <img className="navbar__logo" src={logo} alt="Photosnap company logo" />
            </Link>
            <div className="navbar__links">
                <PageLinks />
            </div>
            <Button text="Get an invite" />
            <a href="#" 
               className={`navbar__mobile__btn ${menuOpen ? 'change' : ''}`} 
               aria-label="Button for mobile menu deployment" 
               onClick={() => setMenuOpen(!menuOpen)}
            >
                <img className={`navbar__mobile__btn--open ${menuOpen ? 'change' : ''}`} src={open} /*onClick={mobileNavOpen} */ alt="" />
                <img className={`navbar__mobile__btn--close ${menuOpen ? 'change' : ''}`} src={close} /*onClick={mobileNavClose}*/ alt="" />
            </a>
            <div className={`navbar__mobile__menu ${menuOpen ? 'deployed' : ''}`}>
                <PageLinks />
                <Button text="Get an invite" />
            </div>
            <div className={`overlay ${menuOpen ? 'active' : ''}`} />
        </div>
    )
}

export default Navbar
