import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import data from '../../stories.json';

const StoryThumbnailLink = ({ image, title, author }) => {
    const [story] = useState([data])
    console.log(story)

    return (
        <Link to="/stories">
            <div className="story__thumbnail">
                <img className="story__thumbnail__backdrop" src={image} />
                <div className="story__thumbnail__textbox">
                    <h6 className="story__thumbnail__textbox--title">{title}</h6>
                    <small className="story__thumbnail__textbox--author">by {author}</small>
                </div>
            </div>
        </Link>
    )
}

export default StoryThumbnailLink
