import React, { useEffect, useRef } from 'react'
import { Link } from 'react-router-dom';
import { PageLinks, Button } from './';
import logo from '../images/shared/desktop/logo.svg';
import open from '../images/shared/mobile/menu.svg';
import close from '../images/shared/mobile/close.svg';

const Navbar = () => {
    let { body } = document,
        overlay = useRef(),
        mobileMenu = useRef();

        let yh = document.querySelector('.navbar__mobile__btn');
        

    useEffect(() => {
        hideMobileNav()
        console.log(yh)
    }, [])

    const hideMobileNav = () => {
        let links = Array.from(mobileMenu.current.children);
        console.log(links)

        links.forEach(link => link.addEventListener('click', () => (
            mobileNavClose()
        )))
        
    }
    
    const mobileNavOpen = (e) => {
        e.target.parentElement.classList.add('change');
        e.target.nextSibling.classList.remove('spin');
        mobileMenu.current.classList.toggle('deployed');
        overlay.current.classList.add('active');
        body.style.overflowY = 'hidden';
        body.parentElement.style.overflowY = 'hidden';
    }

    const mobileNavClose = e => {
        setTimeout(function() {
            e.target.parentElement.classList.remove('change')
        }, 850);
        e.target.classList.add('spin');
        mobileMenu.current.classList.toggle('deployed');
        mobileMenu.current.style.transitionDelay = '450ms';
        overlay.current.classList.remove('active');
        overlay.current.style.transitionDelay = '450ms';
        body.style.overflowY = 'visible';
        body.parentElement.style.overflowY = 'visible';
        e.persist();
    }

    return (
        <div className="navbar">
            <Link to="/">
                <img className="navbar__logo" src={logo} alt="Photosnap company logo" />
            </Link>
            <div className="navbar__links">
                <PageLinks />
            </div>
            <Button text="Get an invite" />
            <a href="#" className="navbar__mobile__btn" aria-label="Button for mobile menu deployment">
                <img className="navbar__mobile__btn--open" src={open} onClick={mobileNavOpen} alt="" />
                <img className="navbar__mobile__btn--close" src={close} onClick={mobileNavClose} alt="" />
            </a>
            <div className="navbar__mobile__menu" ref={mobileMenu} onClick={hideMobileNav}>
                <PageLinks />
                <Button text="Get an invite" />
            </div>
            <div className="overlay" ref={overlay} />
        </div>
    )
}

export default Navbar
