import React from 'react';
import { Button } from '../';
import picture from '../../images/home/desktop/create-and-share.jpg';
import arrow from '../../images/shared/desktop/arrow-white.svg';

const Hero = () => {
    return (
        <div className="hero__section">
            <div className="hero__section__textbox">
                <h1 className="hero__section__textbox--heading">
                    Create and share your photo Stories
                </h1>
                <p className="hero__section__textbox--subheading">
                    Photosnap is a platform for photographers and visual storytellers. We make it easy to share photos, tell stories and connect with others.
                </p>
                <Button text="Get an invite" arrow={arrow} underline="yes" />
            </div>
            <div className="hero__section__image__container">
                <img 
                    className="hero__section__image__container--image"
                    src={picture}
                    alt="Man admiring nature with a camera"
                />
            </div>
        </div>
    )
}

export default Hero
