import React from 'react';

const StoryHero = ({ hero }) => {
    return (  
        <div className="stories__hero">
            {hero.map(({ illustration, title, date, author, subheading }) => (
                <>
                <div className="stories__hero__textbox">
                    <small className="stories__hero__textbox--featured">Last Month's Featured Story</small>
                    <h1 className="stories__hero__textbox--heading">{hero.title}</h1>
                    <span className="stories__hero__textbox--date">{hero.date}</span>
                    <span className="stories__hero__textbox--author">by {hero.author}</span>
                    <p className="stories__hero__textbox--synopsis">{hero.subheading}</p>
                </div>
                </>
            ))}
            {/* <img 
                className="stories__hero--backdrop" 
                
                alt={`${hero.title} article`} 
            />
             */}
            StoryHero
        </div>
    )
}

export default StoryHero;
