import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import data from '../../stories.json';

const StoryThumbnailLink = () => {
    const [stories, setStory] = useState([...data])
    console.log(stories)
    console.log('hi')
    return (
        <div>
        {stories.map(({story: {illustration, title, author} }) => (
            <Link to="/stories">
            <div className="story__thumbnail">
                <img className="story__thumbnail__backdrop" src={illustration} />
                <div className="story__thumbnail__textbox">
                    <h6 className="story__thumbnail__textbox--title">{story.title}</h6>
                    <small className="story__thumbnail__textbox--author">by {story.author}</small>
                </div>
            </div>
        </Link>
        ))}
            </div>
    )
}

export default StoryThumbnailLink
